import React from 'react';
import github from '../assets/github_icon.png';
import facebook from '../assets/facebook_icon.png';
import twitter from '../assets/twitter_icon.png';
import metamask_icon from '../assets/metamask_icon.png';

const Hero = () => {
  const myAdress = "https://etherscan.io/address/0xc6Ace5657A412E052ed992FB56119267752Fa2ce";

  return (
    <div className="bg-[url('https://cdn.pixabay.com/photo/2022/03/01/02/51/galaxy-7040416_960_720.png')] bg-no-repeat bg-cover">
      <div className="flex flex-col justify-center items-center mx-auto py-10">
        <h1 className="text-white text-5xl font-bold text-center">
          A.I Arts <br />
          <span className="text-gradient">NFTs </span>
          Collection
        </h1>

        <p className="text-white font-semibold text-sm mt-3">Mint and Collect the hottest NFTs around.</p>

        <p className="text-white text-sm font-medium text-center mt-3">
          Explore the fascinating world of AI-generated NFTs and join our exclusive collection. Don't miss out on the latest trends in digital art!
        </p>

        <button className="shadow-xl shadow-blck text-white bg-[#e32970] hover:bg-[#bd255f] p-2 rounded-full cursor-pointer my-4">
          Mint NOW
        </button>

        <a className="flex justify-center items-center space-x-2 bg-[#000000ad] rounded-full my-4 pr-3 cursor-pointer" href={myAdress} target="_blank">
          <img className="w-11 h-11 object-contain rounded-full" src={metamask_icon} alt="" />
          <div className="flex flex-col font-semibold text-white text-sm">
            <span>0xc6Ace....2ce</span>
            <span className="text-[#e32970]">Sezer</span>
          </div>
        </a>

        {/* ... (rest of your code) */}
      </div>
    </div>
  );
}

export default Hero;
